import React, { useState } from "react";

function CreateArea() {

  const [title, setTitle] = useState("");

  return (
    <div>
      <form>
        <input
          name="title"
          placeholder="Title"
          onChange={(e) => { setTitle(e.target.value); console.log(title) }}

        />
        <textarea name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
