import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [noteArray, setNoteArray] = useState([]);


  function onAdd(noteItems) {
    setNoteArray((prevNote) => {
      return [...prevNote, noteItems]
    })
  }

  // function onDelete(targetId) {
  //   setNoteArray((prevNote) => {
  //     return prevNote.filter((items, index) => {
  //       return index !== targetId;
  //     })
  //   })
  //   console.log("onDelete has been triggered")
  // }

  function onDelete(targetId) {

    setNoteArray(prevValues => {
      return prevValues.filter((items, index) => {
        return index !== targetId
      })
    })
    console.log("onDelete has been triggered")
  }


  return (
    <div>
      <Header />
      <CreateArea
        onAddbtnClick={onAdd}
      />
      {noteArray.map((card, index) => {
        return (
          <Note
            key={index}
            targetId={index}
            heading={card.title}
            content={card.textArea}
            delete={onDelete}
          />
        )
      })}
      <Footer />
    </div>
  );
}

export default App;
