import React, { useState } from "react";

function CreateArea() {

  const [title, setTitle] = useState("");
  const [textArea, setTextArea] = useState("");

  // function handleTitleChange(e) {
  //   var titleChange = e.target.value
  //   setTitle(titleChange);
  // }

  // function handleTextAreaChange(e) {
  //   var textAreaChange = e.target.value;
  //   setTextArea(textAreaChange);
  //   console.log(textArea);
  // }


  function handleTextChange(e) {

    const { name: inputName, value: nValue } = e.target;

    if (inputName === "title") {
      setTitle(() => {
        var titleChange = e.target.nValue;
        return titleChange;
      })
    }
    else if (inputName === "content") {
      setTextArea(() => {
        var textAreaChange = e.target.nValue;
        return textAreaChange;
      })
    }
  }



  return (
    <div>
      <form>
        <input
          name="title"
          placeholder="Title"
          onChange={handleTextChange}
        />
        <textarea
          name="content"
          placeholder="Take a note..."
          rows="3"
          onChange={handleTextChange}
        />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
